import React, { createContext, useReducer } from 'react';
import { FiltersData } from '../Filters/Filters';
import JobOffersReducer from './JobOffersReducer';
import { ActionTypes, JobTypes, State } from './JobOffersUtils';

interface Props {
  children: React.ReactNode;
}

const initialState = {
  filtersData: {
    city: '',
    searchTerm: '',
    area: '',
    jobTypes: [JobTypes.ALL_JOBS],
    sallaryRange: [10, 100000],
  },
};

const JobOffersContext = createContext<{
  state: State;
  dispatch: React.Dispatch<any>;
}>({
  state: initialState,
  dispatch: () => null,
});

const JobOffersProvider: React.FC<Props> = (props) => {
  const [state, dispatch] = useReducer(JobOffersReducer, initialState);

  const updateFiltersData = (filtersData: FiltersData) => {
    dispatch({ type: ActionTypes.UPDATE_FILTERS_DATA, filtersData: state.filtersData });
  };

  return <JobOffersContext.Provider value={{ state, dispatch }}>{props.children}</JobOffersContext.Provider>;
};

export { JobOffersContext, JobOffersProvider };
