import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEnvelope, faLock } from '@fortawesome/free-solid-svg-icons';
import { FC } from 'react';
import Button from '../Button/Button';
import loginPhoto from '../../images/login-photo.png';
import './_Login.scss';
import FormRedirectButton from '../Form/FormRedirectButton';
import FormInput from '../Form/FormInput';

const Login: FC = () => {
    const handleOnSubmit = () => {
        console.log('submit');
    }

    return (
        <div className='login-container'>
            <div className='login-container-left'>
                <img src={loginPhoto} />
            </div>
            <form method='POST' onSubmit={handleOnSubmit} className='login-container-right'>
                <h2>Prisijungimas</h2>

                <FormInput placeholder='El. Paštas' type='text' icon={faEnvelope} />
                <FormInput placeholder='Slaptažodis' type='text' icon={faEnvelope} />

                <div className='form-input-container'>
                    <input placeholder='Slaptažodis' className='form-input' type='password' />
                    <span className='form-input-icon'>
                        <FontAwesomeIcon icon={faLock}/>
                    </span>
                </div>
                <Button styling='form-button'>
                    Prisijungti
                </Button>

                <div className='forgot-logins-container'>
                    <span className='forgot-logins-container-text'>
                        Pamiršote
                    </span>
                    <Button styling='forgot-logins-container-link' href='/'>
                        prisijungimo varda ar slaptažodi?
                    </Button>
                </div>

                <FormRedirectButton buttonText='Užsiregistruoti' redirectUrl='/registration'/>
            </form>
        </div>
    )
}

export default Login;
