import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

interface FormInputProps {
    placeholder: string;
    type: string;
    icon?: any;
    styling?: {
        container: string;
        input: string;
        icon: string;
    }
}

const FormInput = ({placeholder, type, icon, styling}: FormInputProps) => {
    return (
        <div className={styling?.container ? 'form-input-container' : styling?.container}>
            <input 
                placeholder={placeholder}
                className={styling?.input ? 'form-input' : styling?.input}
                type={type} 
            />
            {icon &&
                <span className={styling?.icon ? 'form-input-icon' : styling?.icon}>
                    <FontAwesomeIcon icon={icon}/>
                </span>
            }
        </div>
    )
}

export default FormInput;
