import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

interface FormInputProps {
  placeholder: string;
  type: string;
  icon?: any;
  styling?: {
    container?: string;
    input?: string;
    icon?: string;
  };
  value: string;
  onValueChange: void;
}

const FormInput = ({ placeholder, type, icon, styling }: FormInputProps) => {
  return (
    <div className={styling?.container ? styling?.container : 'form-input-container'}>
      <input placeholder={placeholder} className={styling?.input ? styling?.input : 'form-input'} type={type} />
      {icon && (
        <span className={styling?.icon ? styling?.icon : 'form-input-icon'}>
          <FontAwesomeIcon icon={icon} />
        </span>
      )}
    </div>
  );
};

export default FormInput;
