import { faSearch } from '@fortawesome/free-solid-svg-icons';
import './_Filters.scss';
import FormInput from '../Form/FormInput';
import DropDown from '../Form/Dropdown';
import Checkboxes from '../Form/Checkboxes';
import { Slider, Stack } from '@mui/material';
import Button from '../Button/Button';
import { useState } from 'react';

const Filters: React.FC = () => {
  const dummyCity = ['Vilnius', 'Kaunas', 'Klaipėda'];
  const dummyArea = ['Informacinės technologijos', 'Apskaita', 'Pardavimai'];
  const dummyCheckboxes = [
    {
      name: 'Darbas pilnu etatu',
    },
    {
      name: 'Darbas puse etato',
    },
    {
      name: 'Trumpalaikis darbas',
    },
    {
      name: 'Visi darbai',
      isDefaultChecked: true,
    },
  ];

  const [searchTerm, setSearchTerm] = useState('');

  return (
    <div className="filters-container">
      <h3>Filtrai</h3>
      <FormInput
        placeholder="Paieška.."
        type="text"
        icon={faSearch}
        styling={{
          container: 'filters-input-wrapper',
          input: 'filters-input',
          icon: 'filters-input-icon',
        }}
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <DropDown styling="filters-input-wrapper" label="Miestas" defaultValue="Visi miestai" menuItems={dummyCity} />
      <DropDown styling="filters-input-wrapper" label="Sritis" defaultValue="Visos sritis" menuItems={dummyArea} />

      <div className="filters-input-wrapper">
        <Checkboxes checkboxes={dummyCheckboxes} />
      </div>

      <div className="filters-input-wrapper">
        <Stack spacing={2} direction="row" alignItems="center">
          <span className="slider-label">Min. alga</span>
          <Slider value={[5, 10000]} valueLabelDisplay="auto" />
          <span className="slider-label">Max. alga</span>
        </Stack>
      </div>

      <div className="filters-button-wrapper">
        <Button styling="filters-button">Ieškoti</Button>
      </div>
    </div>
  );
};

export default Filters;
